<template>
    <view class="share-container" v-if="visible">

        <view class="mask" :class="{ show: show }" @click="close"></view>

        <view class="share-bottom-container" :class="{ show: show }" >
            <view class="share-platform-list">
                <view class="share-item" @click="shareWechat">
                    <image src="data:image/png;base64,"></image>
                    <view>微信</view>
                </view>
                <view class="share-item" @click="shareTimeline">
                    <image src="data:image/png;base64,"></image>
                    <view>朋友圈</view>
                </view>
                <!--<view class="share-item" @click="shareQQ">-->
                    <!--<image src="data:image/png;base64,"></image>-->
                    <!--<view>QQ</view>-->
                <!--</view>-->
            </view>
            <view class="button" @click="close">取消</view>
        </view>

        <!--<view class="success-toast" v-if="shareSuccess" >-->
            <!--<image class="icon" src="/static/share/share_success.png"></image>-->
            <!--<view class="text">分享成功</view>-->
        <!--</view>-->

    </view>
</template>

<script>
    export default {
        name: "Share",

        data () {
            return {
                show: false,

                shareSuccess: false,
            }
        },

        props: {
            url: {
                type: String,
                required: true
            },
            title: {
                type: String,
            },
            summary: {
                type: String,
                required: true,
            },
            imageUrl: {
                type: String,
                default: 'https://myplanets-user.oss-cn-hangzhou.aliyuncs.com/PICTURE/201812211029207603.png',
            },
            visible: Boolean,
        },

        watch: {
            visible (visible) {
                if (visible) {
                    setTimeout(() => {
                        this.show = true
                    }, 200)
                }
            }
        },

        methods: {
            close () {
                this.show = false;
                setTimeout(() => {
                    this.shareSuccess = false;
                    this.$emit('close');
                }, 300);
            },

            share (provider, scene) {
                uni.share({
                    provider,
                    scene,
                    title: this.title,
                    type: 0,
                    href: this.url,
                    summary: this.summary,
                    imageUrl: this.imageUrl,
                    success: res => {
                        this.shareSuccessHandle(res);
                    },
                    fail: err => {
                        this.shareFailHandle(err);
                    }
                });
            },

            shareWechat () {
                this.share('weixin', 'WXSceneSession');
            },

            shareTimeline () {
                this.share('weixin', 'WXSenceTimeline');
            },

            shareQQ () {
                uni.getProvider({
                    service: 'share',
                    success: res => {
                        console.log(res.provider)
                        if (~res.provider.indexOf('qq')) {
                            this.share('qq');
                        }
                    }
                });
            },

            shareSuccessHandle (res) {
                console.log(JSON.stringify(res))
            },

            shareFailHandle (err) {
                console.log(JSON.stringify(err))
            }

        },

    }
</script>

